const http=require('http');
const server=http.createServer((req,res)=>{
    
    if(req.url==='/about')
    {
        res.end('it is short history about us');
    }
    else if(req.url==="/")
    {
        res.end("This is our home page")
    }
    else 
    {
        res.end(`<h1> Oops Page not found </h1> <a href="/"> back to home </a>`)
    }
})
server.listen(5000)